pragma solidity ^0.4.23;

contract ConnectFour {

    address red;
    address yellow;

    address public turn;
    address[6][7] public grid;

    uint public bet;

    uint8 counter = 0;

    event NextPlayer(address player);
    event Winner(address player);
    event Draw();

    // initialize red and set turn
    constructor() public payable {
        red = msg.sender;
        turn = red;
	bet = msg.value;
    }

    // initialize yellow and fire NP event
    function joinGame() public payable {
        require(msg.sender != red);
        require(yellow == address(0));
	//	require(msg.value == bet);
        yellow = msg.sender;
        emit NextPlayer(yellow);
    }

    // return grid
    function getGrid() public view returns(address[6][7]) {
        return grid;
    }

    // set grid element to proper address, set turn
    // and fire NP event
    function dropDisc(uint8 column) public {
      require( turn == msg.sender );
      for (uint8 i=0; i < 6; i++) {
	if (grid[column][i] == address(0)) {
	  grid[column][i] = msg.sender;
	  counter++;
	  checkForWinner(turn, column, i);
	  if (turn == red) {
	    turn = yellow;
	  } else {
	    turn = red;
	  }
	  break;	    
	}
      }
      emit NextPlayer(turn);
    }

    function checkForWinner(address player, uint8 column, uint8 row) private {
      uint8 count = 0;
      uint8 currentRow = row;
      uint8 currentColumn = column;

      // check column
      for (uint8 j=0 ;j<6 ;j++) {
	if (grid[column][j] == player) {
	  count++;
	  if (count == 4) {
	    sendPayout(player);
	    return;
	  }
	} else {
	  count = 0;
	}
      }
      count = 0;

      // check row
      for (uint8 k=0; k<7; k++) {
	if (grid[k][row] == player) {
	  count++;
	  if (count == 4) {
	    sendPayout(player);
	    return;
	  }
	} else {
	  count = 0;
	}
      }
      count = 0;

      // check left to right diagonals
      while (currentRow != 0 && currentColumn != 0) {
	currentRow--;
	currentColumn--;
      }
      while (currentRow <= 5 && currentColumn <= 6) {
	if (grid[currentColumn][currentRow] == player) {
	  if (count == 3) {
	    sendPayout(player);
	    return;
	  }
	  count++;
	} else {
	  count = 0;
	}
	currentRow++;
	currentColumn++;
      }
      currentRow = row;
      currentColumn = column;
      count = 0;

      // check right to left diagonals
      while (currentRow != 0 && currentColumn != 6) {
	currentRow--;
	currentColumn++;
      }
      while (currentRow <= 5 && currentColumn >= 0 && currentColumn < 7) {
	if (grid[currentColumn][currentRow] == player) {
      	  if (count == 3) {
      	    sendPayout(player);
      	    return;
      	  }
      	  count++;
      	} else {
      	  count = 0;
	}
      	currentRow++;
      	currentColumn--;
      }
      currentRow = row;
      currentColumn = column;
      count = 0;

      // check for draw and send each player their money back
      if (counter == 7*6) {
	red.transfer(address(this).balance/2);
	yellow.transfer(address(this).balance);
	emit Draw();
      }

    }

    function sendPayout(address player) private {
      emit Winner(player);
      player.transfer(address(this).balance);
    }
}
